import React, { useState } from "react";
import { getCollectionsByIndex } from "helpers/collections";
import { Image, Alert } from "antd";
import { useNFTTokenIds } from "hooks/useNFTTokenIds";
import { useParams } from "react-router-dom";
import { Link } from "react-router-dom";

const styles = {
  banner: {
    background : "url('/images/bg/01.jpg') bottom",
    marginBottom: "40px",
  },
  logo: {
    height: "115px",
    width: "115px",
    borderRadius: "50%",
    border: "solid 4px white",
  },

};


function Collection() {

  const params = useParams();
  const { id: collectionId } = params; 

  
  const collection = getCollectionsByIndex("0x1", collectionId);
  const [inputValue, setInputValue] = useState(collection.addrs);

  const fallbackImg =
    "data:image/png;base64,";
  const { NFTTokenIds, totalNFTs, fetchSuccess } = useNFTTokenIds(inputValue);


  return (
    <>

      <div>
        {inputValue !== "explore" && totalNFTs !== undefined && (
          <>
            {!fetchSuccess && (
              <>
                <Alert
                  message="Unable to fetch all NFT metadata... We are searching for a solution, please try again later!"
                  type="warning"
                />
                <div style={{ marginBottom: "10px" }}></div>
              </>
            )}


            <section className="bg-half-170 d-table w-100" style={styles.banner}>
                <div className="bg-overlay bg-gradient-overlay-2"></div>
                <div className="container">
                    <div className="row mt-5 justify-content-center">
                        <div className="col-12">
                            <div className="title-heading text-center">
                              <Image
                                preview={false}
                                src={NFTTokenIds[0]?.image || "error"}
                                fallback={fallbackImg}
                                alt=""
                                style={styles.logo}
                              />
                                <h5 className="heading fw-semibold sub-heading text-white title-dark">{`${NFTTokenIds[0]?.name}`}</h5>
                                {/* <p className="text-white-50 para-desc mx-auto mb-0">{`${NFTTokenIds[0]?.description}`}</p> */}
                                <div
                                  style={{
                                    fontSize: "15px",
                                    color: "#9c9c9c",
                                    fontWeight: "normal",
                                  }}
                                >
                                  Collection Size: {`${totalNFTs}`} Items
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </section>

            <div className="position-relative">            
                <div className="shape overflow-hidden text-white">
                    <svg viewBox="0 0 2880 48" fill="none" xmlns="http://www.w3.org/2000/svg">
                        <path d="M0 48H1437.5H2880V0H2160C1442.5 52 720 0 720 0H0V48Z" fill="currentColor"></path>
                    </svg>
                </div>
            </div>
              
          </>
        )}
        
        <div className="container mt-100 mt-60">                 
          <div className="row row-cols-xl-4 row-cols-lg-3 row-cols-sm-2 row-cols-1 g-4" id="grid">
          {inputValue !== "explore" &&
              NFTTokenIds.slice(0, 20).map((nft, index) => (
              <div className="col picture-item" style={{ cursor : "pointer" }} key={index} onClick={() => setInputValue(nft?.addrs)}>
                  <div className="card nft-items nft-primary rounded-md shadow overflow-hidden mb-1 p-3">
                      

                      <div className="nft-image rounded-md mt-3 position-relative overflow-hidden">
                          
                          <Link to={"/item/"+nft.token_address}><img  style={{ width:"100%", height: "auto" }} src={nft.image || "error"} fallback={fallbackImg} alt="" className="img-fluid" /></Link>
                          
                      </div>

                      <div className="card-body content position-relative p-0 mt-3">
                          


                          <div className="d-flex justify-content-between mt-2" style={{ fontWeight : "600" }}>
                              <small className="text-dark">{nft.name}</small>
                              <small className="text-dar">Price</small>
                          </div>

                          <div className="d-flex justify-content-between mt-2">
                              <p className="title text-dark h6">{`#${nft.token_id}`}</p>
                              <small className="title text-dark h6">20.5 ETH</small>
                          </div>
                          
                          <Link to={"/item/"+nft.token_address} className="btn btn-primary">Buy Now</Link>
                      </div>
                  </div>
              </div>
            ))}
          </div>
        </div>
        
      </div>

    </>
  );
}

export default Collection;