import React from "react";
import { getCollectionsByChain } from "helpers/collections";
import {Link} from "react-router-dom";
import Header from "components/Header";
import Footer from "components/Footer";
import { useMoralisDapp } from "providers/MoralisDappProvider/MoralisDappProvider";



const styles = {
  NFTs: {
    display: "flex",
    flexWrap: "wrap",
    WebkitBoxPack: "start",
    justifyContent: "flex-start",
    margin: "0 auto",
    maxWidth: "1000px",
    gap: "10px",
  },
  banner: {
    background : "url('/images/bg/03.jpg') bottom",
    marginBottom: "40px",
  },
};

  

function NFTTokenIds({ inputValue, setInputValue }) {

  const fallbackImg =
    "data:image/png;base64,";
  
  const { chainId } =
    useMoralisDapp();
  
  
  const NFTCollections = getCollectionsByChain(chainId);

  return (
    <>
    <Header />

      <section className="bg-half-170 d-table w-100" style={styles.banner}>
        <div className="bg-overlay bg-gradient-overlay-2"></div>
          <div className="container">
              <div className="row mt-5 justify-content-center">
                  <div className="col-12">
                      <div className="title-heading text-center">
                        
                          <h5 className="heading fw-semibold sub-heading text-white title-dark">Explore Collections</h5>
                          
                      </div>
                  </div>
              </div>
          </div>
      </section>

      <div className="position-relative" style={{ marginBottom: "30px" }}>            
        <div className="shape overflow-hidden text-white">
          <svg viewBox="0 0 2880 48" fill="none" xmlns="http://www.w3.org/2000/svg">
              <path d="M0 48H1437.5H2880V0H2160C1442.5 52 720 0 720 0H0V48Z" fill="currentColor"></path>
            </svg>
        </div>
      </div>

      <div>
        
        <div className="container mt-100 mt-60">                 
          <div className="row row-cols-xl-4 row-cols-lg-3 row-cols-sm-2 row-cols-1 g-4" id="grid">
          {inputValue === "explore" &&
                NFTCollections?.map((nft, index) => (
              <div className="col picture-item" style={{ cursor : "pointer" }} key={index} onClick={() => setInputValue(nft?.addrs)}>
                  <div className="card nft-items nft-primary rounded-md shadow overflow-hidden mb-1 p-3">
                      

                      <div className="nft-image rounded-md mt-3 position-relative overflow-hidden">
                          <Link to={"/collection/"+index}><img  style={{ width:"100%", height: "auto" }} src={nft.image || "error"} fallback={fallbackImg} alt="" className="img-fluid" /></Link>
                          
                      </div>

                      <div className="card-body content position-relative p-0 mt-3">
                          <Link to={"/collection/"+index} className="title text-dark h6" style={{ display:"block", textAlign:"center" }}>{nft.name}<img style={{ display:"inline", width: "20px", margin:'0 0 5px 2px', height: "20px"}}  src="/images/verify.png" alt="Verified Collection" title="Verified Collection" /></Link>
                      </div>
                  </div>
              </div>
            ))}
          </div>
        </div>
       
      </div>
      <Footer />
    </>
  );
}

export default NFTTokenIds;